        is550Printer:                  throwError,
        getConsumableInfoIn550Printer: throwError,

        getPrintersAsync:			        throwError,
        openLabelFileAsync:			        throwError,
        printLabelAsync:			        throwError,
        printLabel2Async:			        throwError,
